import React from 'react'
import Team from '../components/Team.js'
import SideTeams from '../components/SideTeams.js'
import { connect } from 'react-redux'
import { Switch, Route } from 'react-router-dom'

const TeamsContainer = ({ teams }) => {
    return (
      <div className="TeamsContainer">
        <SideTeams team={team} />

        <Switch>
          <Route exact path="/team/:id" render={(routerProps)=> {
            const team = team.find(team => team.id === parseInt(routerProps.match.params.id))
            return <Team {...routerProps} team={team}/>
          }}/>
        </Switch>

      </div>
    )
}

