import React from 'react';
import { connect } from 'react-redux';
import { createTeamMember } from '../actions/teamMembers';

class TeamSelect extends React.Component {
  //props are collaborator and currentTeams

  handleSubmit(e) {
    e.preventDefault();
    this.props.createTeamMember(this.props.collaborator.id,e.target.value)
  }

  render() {
    return (
      <div className="TeamSelect">
        <form onSubmit={this.handleSubmit}>
          <label>
            Select a Team:
            <select onChange={this.handleChange}>
              {this.props.availableTeams.map( team =>
                <option value={team}>{team.name}</option>
              )}
            </select>
          </label>
          <input type="submit" value="Join Team" />
        </form>
      </div>
    )
  }
}

export default connect(null,{createTeamMember})(TeamSelect)
