const collaboratorsReducer = (state = { collaborators: [], loading: false }, action) => {
  switch(action.type) {
    case 'LOADING_COLLABORATORS':
      return {
        ...state,
        collaborators: [...state.collaborators],
        loading: true
      }
    case 'ADD_COLLABORATORS':
      return {
        ...state,
        collaborators: action.collaborators,
        loading: false
      }
    default:
      return state;
  }
}

export default collaboratorsReducer;
