import React from 'react';
import { connect } from 'react-redux';
import { createTeamMember } from '../actions/teamMembers';

class TeamSelect extends React.Component {
  //props are collaborator and currentTeams

  state = {
    collaborator: this.props.collaborator,
  }

  availableTeams(allTeams,memberTeams) {
    return allTeams.filter(team => !memberTeams.includes(team) );
  }

  handleChange(e) {
    this.setState({team: e.target.value});
  }

  handleSubmit(e) {
    e.preventDefault();

  }

  render() {
    return (
      <div className="TeamSelect">
        <form onSubmit={this.handleSubmit}>
          <label>
            Select a Team:
            <select onChange={this.handleChange}>
              {this.availableTeams(this.props.teams,this.props.currentTeams).map( team =>
                <option value={team}>{team.name}</option>
              )}
            </select>
          </label>
          <input type="submit" value="Join Team" />
        </form>
      </div>
    )
  }
}

const mapStateToProps = ({ teams }) => ({ teams })

export default connect(mapStateToProps,{createTeamMember})(TeamSelect)
