import React, { Component } from 'react'

class NewCollaborator extends Component {
  state = {
    name: '',
    role: '',
    skills: '',
  };

  handleOnChange = event => {
    this.setState({
      name: event.target.value,
      role: event.target.value,
      skills: event.target.value
    })
  }

  handleOnSubmit = event => {
    event.preventDefault();
    this.props.createCollaborator(this.state)
    this.setState({
      name: '',
      role: '',
      description: ''
    })
  }

  render() {
    return(
      <div className="newCollaborator">
        <form onSubmit={this.handleOnSubmit} >
          <p>Name:<input name="description" type="text" onChange={this.handleOnChange} value={this.state.name} /></p>
          <p>Role:<input name="role" type="text" onChange={this.handleOnChange} value={this.state.role} /></p>
          <p>Description:<input name="description" type="text" onChange={this.handleOnChange} value={this.state.skills} /></p>
          <input type="submit" onSubmit={this.handleOnSubmit} />
        </form>
      </div>
    )
  }
}

export default NewCollaborator
